toolUseResult.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Fresh green health theme */
    --background: 145 20% 97%;
    --foreground: 150 30% 15%;

    --card: 0 0% 100%;
    --card-foreground: 150 30% 15%;

    --popover: 0 0% 100%;
    --popover-foreground: 150 30% 15%;

    --primary: 152 60% 45%;
    --primary-foreground: 0 0% 100%;

    --secondary: 30 85% 55%;
    --secondary-foreground: 0 0% 100%;

    --muted: 145 15% 92%;
    --muted-foreground: 150 15% 45%;

    --accent: 152 45% 92%;
    --accent-foreground: 152 60% 30%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 150 20% 88%;
    --input: 150 20% 88%;
    --ring: 152 60% 45%;

    --radius: 1rem;

    /* Custom tokens */
    --gradient-primary: linear-gradient(135deg, hsl(152 60% 45%), hsl(168 55% 50%));
    --gradient-secondary: linear-gradient(135deg, hsl(30 85% 55%), hsl(40 90% 60%));
    --gradient-bg: linear-gradient(180deg, hsl(145 25% 97%), hsl(150 20% 94%));
    --gradient-card: linear-gradient(145deg, hsl(0 0% 100%), hsl(145 15% 98%));

    --shadow-soft: 0 4px 20px -4px hsl(152 60% 45% / 0.15);
    --shadow-card: 0 8px 30px -10px hsl(150 30% 15% / 0.1);
    --shadow-glow: 0 0 40px hsl(152 60% 45% / 0.2);

    /* Nutrition colors */
    --calories: 0 75% 55%;
    --protein: 210 75% 55%;
    --carbs: 45 90% 50%;
    --fat: 320 70% 55%;
  }

  .dark {
    --background: 150 25% 8%;
    --foreground: 145 15% 95%;

    --card: 150 20% 12%;
    --card-foreground: 145 15% 95%;

    --popover: 150 20% 12%;
    --popover-foreground: 145 15% 95%;

    --primary: 152 55% 50%;
    --primary-foreground: 0 0% 100%;

    --secondary: 30 80% 55%;
    --secondary-foreground: 0 0% 100%;

    --muted: 150 15% 18%;
    --muted-foreground: 145 10% 60%;

    --accent: 152 30% 20%;
    --accent-foreground: 152 50% 70%;

    --destructive: 0 70% 50%;
    --destructive-foreground: 0 0% 100%;

    --border: 150 15% 20%;
    --input: 150 15% 20%;
    --ring: 152 55% 50%;

    --gradient-primary: linear-gradient(135deg, hsl(152 55% 50%), hsl(168 50% 45%));
    --gradient-bg: linear-gradient(180deg, hsl(150 25% 8%), hsl(150 20% 6%));
    --gradient-card: linear-gradient(145deg, hsl(150 20% 14%), hsl(150 20% 10%));

    --shadow-soft: 0 4px 20px -4px hsl(152 55% 50% / 0.2);
    --shadow-card: 0 8px 30px -10px hsl(0 0% 0% / 0.3);
    --shadow-glow: 0 0 40px hsl(152 55% 50% / 0.15);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    background: var(--gradient-bg);
    min-height: 100vh;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  .upload-zone {
    @apply relative overflow-hidden rounded-2xl border-2 border-dashed border-primary/30
           bg-gradient-to-br from-accent/50 to-muted/30 p-8
           transition-all duration-300 ease-out cursor-pointer;
  }

  .upload-zone:hover {
    @apply border-primary/60 scale-[1.01];
    box-shadow: var(--shadow-glow);
  }

  .upload-zone.dragging {
    @apply border-primary bg-accent scale-[1.02];
    box-shadow: var(--shadow-glow);
  }

  .nutrition-card {
    @apply rounded-xl p-4 transition-all duration-300;
    background: var(--gradient-card);
    box-shadow: var(--shadow-card);
  }

  .nutrition-card:hover {
    @apply scale-[1.02];
    box-shadow: var(--shadow-soft);
  }

  .result-card {
    @apply rounded-2xl overflow-hidden;
    background: var(--gradient-card);
    box-shadow: var(--shadow-card);
  }

  .stat-ring {
    @apply relative flex items-center justify-center;
  }

  .pulse-animation {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes pulse-glow {
    0%, 100% {
      opacity: 1;
      transform: scale(1);
    }
    50% {
      opacity: 0.7;
      transform: scale(1.05);
    }
  }

  .fade-in {
    animation: fadeIn 0.5s ease-out forwards;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .slide-up {
    animation: slideUp 0.6s ease-out forwards;
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

