        {analysisResult && (
          <div className="mt-8">
            <FoodAnalysisResult data={analysisResult} />

            <div className="mt-6 text-center">
              <Button
                variant="outline"
                onClick={handleReset}
                className="gap-2"
              >
                <Sparkles className="w-4 h-4" />
                åˆ†ææ–°å›¾ç‰‡
              </Button>
            </div>
          </div>
        )}

        {/* Tips Section */}
        {!hasImage && (
          <div className="mt-10 fade-in">
